document.addEventListener("DOMContentLoaded",(function(){$("select").on("change",(function(){console.log($(this).val());const n=$(this).val();$.ajax({method:"GET",url:"https://api.nytimes.com/svc/topstories/v2/"+n+".json?api-key=c7IZd55GMn6VzakaBcCAByRv6prf4rqS"}).done((function(n){const t=n.results;console.log(t),$.each(t,(function(n,t){$("#nyt-articles").append(`\n                <a href="${t.url}">\n                    <article class="stories" style="background-image:url(${t.multimedia[4].url});">      \n                        <p>${t.abstract}</p>\n                    </article>\n                </a>\n                \n                `)}))})).fail((function(){console.log("something went wrong")}))}))}));